import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/login'; // Adjust path if necessary
import Home from './components/home';   // Your Home component

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(true);

  // Fetch login status from the server when the component mounts
  useEffect(() => {
    fetch('/api/user')  // Fetching from your backend
      .then((response) => response.json())
      .then((data) => {
        if (data.username) {
          setIsLoggedIn(true);
        } else {
          setIsLoggedIn(false);
        }
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching user status:', error);
        setLoading(false);
      });
  }, []);

  // Protect routes
  const ProtectedRoute = ({ element }) => {
    if (loading) {
      return <p>Loading...</p>; // Show loading state while checking login status
    }
    return isLoggedIn ? element : <Navigate to="/login" />; // Redirect to login if not logged in
  };

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/home" element={<ProtectedRoute element={<Home />} />} />
        <Route path="*" element={<ProtectedRoute element={<Home />} />} /> {/* Default route */}
      </Routes>
    </Router>
  );
}

export default App;
