import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/login';

function Home() {
  const [username, setUsername] = useState(null); // Default to null
  const [fixtures, setFixtures] = useState([]);

  useEffect(() => {
    // Fetch user data from backend when component mounts
    fetch('/api/user')  // Assuming '/api/user' provides username
      .then(response => response.json())
      .then(data => {
        if (data.username) {
          setUsername(data.username);
        } else {
          setUsername(false); // Set to false if no user is logged in
        }
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
        setUsername(false); // Set to false on error
      });

    // Fetch fixtures data from backend
    fetch('/api/fixtures') // Assuming '/api/fixtures' provides fixtures
      .then(response => response.json())
      .then(data => setFixtures(data))
      .catch(error => console.error('Error fetching fixtures:', error));
  }, []);

  if (username === null) {
    return null; // Render nothing while checking for user
  }

  if (username === false) {
    return <Navigate to="/login" />; // Redirect to login if no user is logged in
  }

  return (
    <div>
      <h1>Welcome, {username}!</h1>
      <h2>Today's Fixtures</h2>
      <ul>
        {fixtures.map((fixture, index) => (
          <li key={index}>
            {fixture.team1} vs {fixture.team2} at {fixture.time}
          </li>
        ))}
      </ul>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/" element={<Home />} />
      </Routes>
    </Router>
  );
}

export default App;
