import React, { useEffect, useState } from 'react';

function App() {
  const [username, setUsername] = useState('Guest'); // Default to 'Guest'
  const [fixtures, setFixtures] = useState([]);

  useEffect(() => {
    // Fetch user data and fixtures from backend when component mounts
    fetch('/api/user')  // Assuming '/api/user' provides username
      .then(response => response.json())
      .then(data => setUsername(data.username))
      .catch(error => console.error('Error fetching user data:', error));

    fetch('/api/fixtures') // Assuming '/api/fixtures' provides fixtures
      .then(response => response.json())
      .then(data => setFixtures(data))
      .catch(error => console.error('Error fetching fixtures:', error));
  }, []);

  return (
    <div>
      <h1>Welcome, {username}!</h1>
      <a href="/logout">Logout</a>

      <h2>Today's Fixtures</h2>

      {fixtures.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>League</th>
              <th>Team 1</th>
              <th>Team 2</th>
              <th>Time</th>
            </tr>
          </thead>
          <tbody>
            {fixtures.map((fixture, index) => (
              <tr key={index}>
                <td>{fixture.league}</td>
                <td>{fixture.team1}</td>
                <td>{fixture.team2}</td>
                <td>{fixture.time}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No fixtures for today.</p>
      )}
    </div>
  );
}

export default App;
