import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';

function App() {
  const [username, setUsername] = useState(null); // Default to null
  const [fixtures, setFixtures] = useState([]);
  const history = useHistory();

  useEffect(() => {
    // Fetch user data from backend when component mounts
    fetch('/api/user')  // Assuming '/api/user' provides username
      .then(response => response.json())
      .then(data => {
        if (data.username) {
          setUsername(data.username);
        } else {
          history.push('/login'); // Redirect to login if no user is logged in
        }
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
        history.push('/login'); // Redirect to login on error
      });

    // Fetch fixtures data from backend
    fetch('/api/fixtures') // Assuming '/api/fixtures' provides fixtures
      .then(response => response.json())
      .then(data => setFixtures(data))
      .catch(error => console.error('Error fetching fixtures:', error));
  }, [history]);

  if (username === null) {
    return null; // Render nothing while checking for user
  }

  return (
    <div>
      <h1>Welcome, {username}!</h1>
      <h2>Today's Fixtures</h2>
      <ul>
        {fixtures.map((fixture, index) => (
          <li key={index}>
            {fixture.team1} vs {fixture.team2} at {fixture.time}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
